import axios from "axios";
import config from '../consts/config'

const MockAdapter = require("axios-mock-adapter");

const mock = new MockAdapter(axios);


const api = axios.create({
  baseURL: config.api_url,
  timeout: 15000,
  headers: {
    Authorization: config.auth_public_token,
    "Access-Control-Allow-Origin": "*"
  }
});

mock.onGet("/videos").reply(200, [{
  imgUrl: 'https://umirzaskov.site/static/84ee5cc5de5e81ad54c1f37c3c1a1359/Beware_Intro.jpg',
  webmUrl: 'https://umirzaskov.site/static/fbc080874ef31c90dd427a9b051ee4e5/Beware_Intro.webm',
  title: "titel"
}])


export { api };
